import axios from 'axios';

API_URL = '/api/v1/list';

export const getItems = () => {
    return axios.get(URL);
}

export const createItem = (item) => {
    return axios.post(URL, item);
}

export const deleteItem = () => {
    return axios.delete('${API_URL}/${id}');
}
